mapper.select:
SELECT DISTINCT emp.*, dep.id AS depid, dep.`name` AS depname, pos.id AS posid, pos.`name` AS posname, pos.enabled AS posenabled, nat.id AS natid, nat.`name` AS natname, ps.id AS psid, ps.`name` AS psname, job.id AS jobid, job.`name` AS jobname, job.enabled AS jobenabled FROM t_employee emp, t_department dep, t_position pos, t_nation nat, t_politics_status ps, t_joblevel job WHERE emp.department_id = dep.id AND emp.position_id = pos.id AND emp.nation_id = nat.id AND emp.politic_id = ps.id AND emp.job_level_id = job.id AND emp.is_delete = FALSE AND emp.work_state = '在职'
AND emp.`name` LIKE concat('%',#{employee.name},'%')
AND emp.politic_id = #{employee.politicId}
AND emp.nation_id = #{employee.nationId}
AND emp.job_level_id = #{employee.jobLevelId}
AND emp.position_id = #{employee.positionId}
AND emp.engage_form= #{employee.engageForm}
AND emp.department_id = #{employee.departmentId}
AND emp.begin_date BETWEEN #{beginDateScope[0]} AND #{beginDateScope[1]}
ORDER BY emp.id

SELECT DISTINCT emp.*, dep.id AS depid, dep.`name` AS depname, pos.id AS posid, pos.`name` AS posname, nat.id AS natid, nat.`name` AS natname, ps.id AS psid, ps.`name` AS psname, job.id AS jobid, job.`name` AS jobname FROM t_employee emp, t_department dep, t_position pos, t_nation nat, t_politics_status ps, t_joblevel job WHERE emp.department_id = dep.id AND emp.position_id = pos.id AND emp.nation_id = nat.id AND emp.politic_id = ps.id AND emp.job_level_id = job.id AND emp.is_delete = FALSE
AND emp.id = #{employeeId}
ORDER BY emp.id

SELECT emp.id, emp.`name`, emp.work_id, dep.id AS depId, dep.`name` AS depName, st.id AS stId, st.`year` AS stYear, st.`month` AS stMonth, st.bonus AS stBonus, st.all_salary AS stAllSalary, st.enabled AS stEnabled FROM t_employee emp, t_department dep, t_salary_table st WHERE emp.department_id = dep.id AND emp.work_state = '在职' AND emp.id = st.employee_id
AND dep.id = #{depId}
AND st.`year` = #{year}
AND st.`month` = #{month}
ORDER BY emp.id

SELECT emp.id, emp.`name`, emp.work_id, dep.id AS depId, dep.`name` AS depName, st.id AS stId, st.`year` AS stYear, st.`month` AS stMonth, st.bonus AS stBonus, st.all_salary AS stAllSalary, st.enabled AS stEnabled FROM t_employee emp, t_department dep, t_salary_table st WHERE emp.department_id = dep.id AND emp.work_state = '在职' AND emp.id = st.employee_id
AND st.`year` = #{year}
AND st.`month` = #{month}
ORDER BY emp.id

SELECT emp.gender AS `name`, COUNT(*) AS `count` FROM t_employee emp WHERE 1 = 1 AND emp.work_state = '在职'
AND emp.department_id = #{depId}
GROUP BY emp.gender

SELECT emp.tiptop_degree AS `name`, COUNT(*) AS `count` FROM t_employee emp WHERE 1 = 1 AND emp.work_state = '在职'
AND emp.department_id = #{depId}
GROUP BY emp.tiptop_degree

SELECT emp.engage_form AS `name`, COUNT(*) AS `count` FROM t_employee emp WHERE emp.is_delete = FALSE AND emp.work_state = '在职'
AND emp.department_id = #{depId}
GROUP BY emp.engage_form

SELECT emp.work_state AS `name`, COUNT(*) AS `count` FROM t_employee emp WHERE emp.is_delete = FALSE
AND emp.department_id = #{depId}
GROUP BY emp.work_state

SELECT emp.wed_lock AS `name`, COUNT(*) AS `count` FROM t_employee emp WHERE emp.is_delete = FALSE AND emp.work_state = '在职'
AND emp.department_id = #{depId}
GROUP BY emp.wed_lock

SELECT nat.`name` AS `name`, COUNT(*) AS `count` FROM t_employee emp, t_nation nat WHERE emp.is_delete = FALSE AND emp.work_state = '在职' AND emp.nation_id = nat.id
AND emp.department_id = #{depId}
GROUP BY nat.`name`

SELECT ps.`name` AS `name`, COUNT(*) AS `count` FROM t_employee emp, t_politics_status ps WHERE emp.is_delete = FALSE AND emp.work_state = '在职' AND emp.politic_id = ps.id
AND emp.department_id = #{depId}
GROUP BY ps.`name`
